import { useState, useRef, useEffect } from 'react';
import './App.css';
import axios from 'axios';

export default function App() {
    const [show, setShow] = useState(false);
    const [selectedGender, setSelectedGender] = useState('');
    const [selectedLanguages, setSelectedLanguages] = useState([]);
    const [selectedSlangs, setSelectedSlangs] = useState([]);
    const [selectedAge, setSelectedAge] = useState('');
    const [data, setData] = useState([]);
    const ref = useRef();

    const handleCheckboxChangeGen = (event) => {
        const { value } = event.target;
        setSelectedGender(value);
    };

    const handleCheckboxChangeLan = (event) => {
        const { value, checked } = event.target;
        setSelectedLanguages((prevSelectedLanguages) =>
            checked
                ? [...prevSelectedLanguages, value]
                : prevSelectedLanguages.filter((language) => language !== value)
        );
    };

    const handleCheckboxChangeSla = (event) => {
        const { value, checked } = event.target;
        setSelectedSlangs((prevSelectedSlangs) =>
            checked ? [...prevSelectedSlangs, value] : prevSelectedSlangs.filter((slang) => slang !== value)
        );
    };

    const handleCheckboxChangeAge = (event) => {
        setSelectedAge(event.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // console.log('Selected Gender:', selectedGender);
        // console.log('Selected Languages:', selectedLanguages);
        // console.log('Selected Slangs:', selectedSlangs);
        // console.log('Selected Age:', selectedAge);
    };

    const[newvalue,setNewValue]=(handleSubmit);
    console.log(newvalue)

    useEffect(() => {
        const url = `http://voicebank.com:8001/api/method/voicebank.voicebank_api.voice_data?language=${selectedLanguages.join(',')}&gender=${selectedGender}&slang=${selectedSlangs.join(',')}&age=${selectedAge}`;
        axios.get(url)
            .then(result => {
                console.log(result.data); // Print the fetched data to the console
                setData(result.data.items || result.data); // Adjust based on the structure
            })
            .catch(err => console.log(err));
    }, [selectedLanguages, selectedSlangs, selectedGender, selectedAge]);

    function handleClick() {
        ref.current.style.display = "none";
    }

    return (
        <div className="body">
            <div className="double-body">
                <div className="navbar-logo-left">
                    <div data-animation="default" data-collapse="medium" data-duration="400" data-easing="ease" data-easing2="ease" role="banner" className="navbar-logo-left-container shadow-three w-nav">
                        <div className="container">
                            <div className="navbar-wrapper">
                                <img src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/664811135221adacc31e6c48_logo1.png" loading="lazy" alt="" />
                                <a href=" " className="navbar-brand w-nav-brand">
                                    <div className="text-block-head">South Indian Cine, Television Artistes and Dubbing Artistes Union</div>
                                    <div className="text-block-2">Voice Bank</div>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
                <section className="body-panel">
                    <div className="left-panel">
                        <img src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/6648137ab5ccbbb591b8f10d_radharavi%201.png" loading="lazy" alt="" className="image-4" />
                        <div>
                            {/* Description text */}
                            <br />
                            <br />— டத்தோ ராதா ரவி
                        </div>
                    </div>
                    <div className="right-panel">
                        <form>
                        {/* <form onSubmit={handleSubmit}> */}
                            <div className="flex-div">
                                <div className="form-container">
                                    <h2>Select Language</h2>
                                    <div className="checkbox-group">
                                        {['Tamil', 'Telugu', 'Kannada', 'Malayalam', 'Hindi', 'English', 'Others'].map((language) => (
                                            <label className="custom-option" key={language}>
                                                <input
                                                    type="checkbox"
                                                    value={language}
                                                    checked={selectedLanguages.includes(language)}
                                                    onChange={handleCheckboxChangeLan}
                                                />{' '}
                                                {language}
                                            </label>
                                        ))}
                                    </div>
                                </div>
                                <div className="form-popup" id="myForm" ref={ref}>
                                    <form className="form-container">
                                        <h1>Send Request</h1>
                                        <label htmlFor="email"><b>Email</b></label>
                                        <input type="text" placeholder="Enter Email" name="email" required />

                                        <label htmlFor="phone"><b>Phone</b></label>
                                        <input type="text" placeholder="Enter Phone" name="phone" required />

                                        <button type="submit" className="btn">Send</button>
                                        <button type="button" className="btn cancel" onClick={handleClick}>Close</button>
                                    </form>
                                </div>
                                <div className="form-container">
                                    <h2>Select Slang</h2>
                                    <div className="checkbox-group">
                                        {['Madurai', 'Madras', 'Coimbatore', 'Nagarkovil', 'General'].map((slang) => (
                                            <label key={slang}>
                                                <input
                                                    type="checkbox"
                                                    value={slang}
                                                    checked={selectedSlangs.includes(slang)}
                                                    onChange={handleCheckboxChangeSla}
                                                />{' '}
                                                {slang}
                                            </label>
                                        ))}
                                    </div>
                                </div>
                                <div className="form-container">
                                    <h2>Select Age</h2>
                                    <div className="checkbox-group">
                                        {['5-10', '10-15', '15-20', '20-25', '25-30', '30-35', '35-40', '40-45', '45-50'].map((ageRange) => (
                                            <label key={ageRange}>
                                                <input
                                                    type="checkbox"
                                                    value={ageRange}
                                                    checked={selectedAge === ageRange}
                                                    onChange={handleCheckboxChangeAge}
                                                />{' '}
                                                {ageRange}
                                            </label>
                                        ))}
                                    </div>
                                </div>
                                <div className="form-container">
                                    <h2>Select Gender</h2>
                                    <div className="checkbox-group">
                                        {['Male', 'Female', 'Others'].map((gender) => (
                                            <label key={gender}>
                                                <input
                                                    type="checkbox"
                                                    value={gender}
                                                    checked={selectedGender === gender}
                                                    onChange={handleCheckboxChangeGen}
                                                />{' '}
                                                {gender}
                                            </label>
                                        ))}
                                    </div>
                                </div>
                            </div>
                            <button type="submit" data-wait="Please wait..." className="submit-button w-button">Search</button>
                        </form>
                        <div className="search-result">
                            <div className="result-section">
                                {data && Array.isArray(data) ? (
                                    data.map((option, index) => (
                                        <div className="card" key={index}>
                                            <div className="mem-no">
                                                <div className="mno">M. No. 111</div>
                                                <img
                                                    src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/66481c047761e1b2e1d6f7b6_Component%202.png"
                                                    loading="lazy" alt="" />
                                            </div>
                                            <div className="card-body">
                                                <img
                                                    src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/655b993d0de1fe87e04cd3c3_hero-image-v4-brix-templates.jpeg"
                                                    loading="lazy" sizes="125px"
                                                    srcSet="https://assets-global.website-files.com/6130fc9db35a715a03742ded/655b993d0de1fe87e04cd3c3_hero-image-v4-brix-templates-p-500.jpeg 500w, https://assets-global.website-files.com/6130fc9db35a715a03742ded/655b993d0de1fe87e04cd3c3_hero-image-v4-brix-templates-p-800.jpeg 800w, https://assets-global.website-files.com/6130fc9db35a715a03742ded/655b993d0de1fe87e04cd3c3_hero-image-v4-brix-templates-p-1080.jpeg 1080w, https://assets-global.website-files.com/6130fc9db35a715a03742ded/655b993d0de1fe87e04cd3c3_hero-image-v4-brix-templates.jpeg 1192w"
                                                    alt="Hero - Elements Webflow Library - BRIX Templates" className="image-5" />
                                                <div className="cb-text">
                                                    <div className="text-block-4"><span className="text-span">Name</span>{option.first_name}</div>
                                                    <div className="text-block-4"><span className="text-span">Gender</span>{option.GenderValue}</div>
                                                    <div className="text-block-4"><span className="text-span">Language</span>{option.LanguageType}</div>
                                                    <div className="text-block-4"><span className="text-span">Slang</span>{option.SlangType}</div>
                                                    <div className="text-block-4"><span className="text-span">Age</span>{option.AgeValue}</div>
                                                </div>
                                            </div>
                                            <div className="audio-card"></div>
                                        </div>
                                    ))
                                ) : (
                                    <p>No data available</p>
                                )}
                            </div>
                        </div>

                        <button className="open-button enquiry w-button div-block-3" onClick={() => setShow(!show)}>Enquiry now</button>
                    </div>
                </section>
                {/* {show ? <Form /> : null} */}
            </div>
        </div>
    );
}
