import React, { useState, useEffect } from 'react';
import './App.css';
export default App = () => {
  const [formState, setFormState] = useState({
    language: '',
    slang: '',
    gender: '',
    age: '',
  });

  const [searchResults, setSearchResults] = useState([]);
  const [showMoreButton, setShowMoreButton] = useState(false);
  const [showForm, setShowForm] = useState(false);

  const handleCheckboxChange = (event) => {
    const { name, value, checked } = event.target;
    setFormState((prev) => {
      const values = prev[name].split(', ').filter((v) => v);
      if (checked) {
        values.push(value);
      } else {
        const index = values.indexOf(value);
        if (index > -1) values.splice(index, 1);
      }
      return { ...prev, [name]: values.join(', ') };
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log('submitbutton worked');

    // Replace with actual search function logic
    const mockSearchResults = [
      { id: 1, name: 'Artist 1' },
      { id: 2, name: 'Artist 2' },
    ];
    setSearchResults(mockSearchResults);
    setShowMoreButton(mockSearchResults.length > 0);
    console.log('backend worked');
  };

  const handleEnquirySubmit = (event) => {
    event.preventDefault();
    const form = event.target;
    if (!form.checkValidity()) {
      form.reportValidity();
      return;
    }
    console.log('submit button worked');

    // Replace with actual enquiry function logic
    alert('Form submitted successfully!');
    setShowForm(false);
  };

  return (
    <div className="body">
      <div className="double-body">
        <div className="navbar-logo-left">
          <div
            data-animation="default"
            data-collapse="medium"
            data-duration="400"
            data-easing="ease"
            data-easing2="ease"
            role="banner"
            className="navbar-logo-left-container shadow-three"
          >
            <div className="container">
              <div className="navbar-wrapper">
                <img
                  src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/664811135221adacc31e6c48_logo1.png"
                  loading="lazy"
                  alt=""
                />
                <a href="#" className="navbar-brand w-nav-brand">
                  <div className="text-block-head">
                    South Indian Cine, Television Artistes and Dubbing Artistes
                    Union
                  </div>
                  <div className="text-block-2">Voice Bank</div>
                </a>
              </div>
            </div>
          </div>
        </div>
        <form id="searchForm" onSubmit={handleSubmit}>
          <section className="body-panel">
            <div className="left-panel">
              <img
                src="https://assets-global.website-files.com/6130fc9db35a715a03742ded/6648137ab5ccbbb591b8f10d_radharavi%201.png"
                loading="lazy"
                alt=""
                className="image-4"
              />
              <div>
                ஒவ்வொரு ஆண்டுக்கும் வெளியாகும் நூற்றுக்கணக்கான
                திரைப்படங்கள், ஆங்கிலத்திலிருந்து தமிழுக்கு வரும் ஹாலிவுட் படங்கள்,
                மற்றும் இந்திய மொழிகளிலிருந்து தமிழுக்கு மொழிமாற்றப்படும் பல
                நூறு படங்கள் என எண்ணற்ற திரைப்படங்களில் பணிபுரியும்
                இரண்டாயிரத்துக்கும் மேற்பட்ட குரல் கலைஞர்களின் சங்கமம் இந்த
                வலைத்தளம். திரைப்படங்கள் தவிர அன்றாடம் ஒளிபரப்பாகும் சுமார் 60
                தொலைக்காட்சி தொடர்களிலும் தங்கள் குரல்களால் பங்கெடுப்பவர்கள்
                தென்னிந்திய திரைப்பட தொலைக்காட்சி பிண்ணனி குரல் கலைஞர்கள்
                சங்கத்தை சேர்ந்த குரல் கலைஞர்கள். கலைத்துறையில் அரும்பெரும்
                பங்காற்றும் குரல் கலைஞர்களின் வரலாற்றையும், இந்நாளைய
                செயல்பாட்டுகளையும் மற்றும் ஒவ்வொரு கலைஞரின் திறமைகளையும்
                இங்கே பதிவு செய்கிறோம்.
                <br />
                <br />— டத்தோ ராதா ரவி
              </div>
            </div>
            <div className="right-panel">
              <div className="flex-div">
                <div className="form-container">
                  <h2>Select Language</h2>
                  <div className="checkbox-group">
                    <input
                      type="text"
                      hidden
                      name="language"
                      id="language"
                      value={formState.language}
                    />
                    {['Tamil', 'Telugu', 'Kannada', 'Malayalam', 'Hindi', 'English', 'Others'].map((lang) => (
                      <label key={lang} className="custom-option">
                        <input
                          type="checkbox"
                          value={lang}
                          name="language"
                          onChange={handleCheckboxChange}
                        />{' '}
                        {lang}
                      </label>
                    ))}
                  </div>
                </div>
                <div className="form-container">
                  <h2>Select Slang</h2>
                  <div className="checkbox-group">
                    <input
                      type="text"
                      hidden
                      name="slang"
                      id="slang"
                      value={formState.slang}
                    />
                    {['Madurai', 'Madras', 'Coimbatore', 'Nagarkovil', 'General'].map((slang) => (
                      <label key={slang}>
                        <input
                          type="checkbox"
                          value={slang}
                          name="slang"
                          onChange={handleCheckboxChange}
                        />{' '}
                        {slang}
                      </label>
                    ))}
                  </div>
                </div>
                <div className="form-container">
                  <h2>Select Gender</h2>
                  <div className="checkbox-group">
                    <input
                      type="text"
                      hidden
                      name="gender"
                      id="gender"
                      value={formState.gender}
                    />
                    {['Male', 'Female', 'Others'].map((gender) => (
                      <label key={gender}>
                        <input
                          type="checkbox"
                          value={gender}
                          name="gender"
                          onChange={handleCheckboxChange}
                        />{' '}
                        {gender}
                      </label>
                    ))}
                  </div>
                </div>
                <div className="form-container">
                  <h2>Select Age</h2>
                  <div className="checkbox-group">
                    <input
                      type="text"
                      hidden
                      name="age"
                      id="age"
                      value={formState.age}
                    />
                    {[
                      '5-10',
                      '10-15',
                      '15-20',
                      '20-25',
                      '25-30',
                      '30-35',
                      '35-40',
                      '40-45',
                      '45-50',
                    ].map((age) => (
                      <label key={age}>
                        <input
                          type="checkbox"
                          value={age}
                          name="age"
                          onChange={handleCheckboxChange}
                        />{' '}
                        {age}
                      </label>
                    ))}
                  </div>
                </div>
              </div>
              <button type="submit" className="submit-button w-button">
                Search
              </button>
              <div id="searchresults" className="search-result">
                {searchResults.map((result) => (
                  <div key={result.id}>{result.name}</div>
                ))}
              </div>
              {showMoreButton && (
                <button type="button" className="btn-more">
                  Load More
                </button>
              )}
            </div>
          </section>
        </form>
        <div className={`form-popup ${showForm ? '' : 'hidden'}`} id="myForm">
          <form className="form-container" id="enquiryForm" onSubmit={handleEnquirySubmit}>
            <h2>Send Request</h2>
            <label htmlFor="cust_name">
              <b>Name</b>
            </label>
            <input
              type="text"
              placeholder="Enter Name"
              id="cust_name"
              name="cust_name"
              required
            />
            <label htmlFor="email_id">
              <b>Email</b>
            </label>
            <input
              type="text"
              placeholder="Enter Email"
              id="email_id"
              name="email_id"
              required
            />
            <label htmlFor="phone_number">
              <b>Phone</b>
            </label>
            <input
              type="text"
              placeholder="Enter Phone"
              id="phone_number"
              name="phone_number"
              required
            />
            <input
              type="hidden"
              id="artist_member_id_list"
              name="artist_member_id_list"
              value=""
            />
            <button type="submit" className="btn" id="sendButton">
              Send
            </button>
            <button type="button" className="btn cancel" onClick={() => setShowForm(false)}>
              Close
            </button>
            </form>
            </div>
            </div>
            </div>
  )
}
